import React from 'react'
import Link from 'next/link';
import { Upload, message, Button } from 'antd';
import "../style/index.css"
import dynamic from 'next/dynamic'
import Luna from '../components/Luna'
import '../style/page_index.css'
import Ske from '../layout/Ske'
import { inject, observer } from "mobx-react"
const RustComponent = dynamic({
  loader: async () => {
    let cc = await import('../public/pkg/wasm_pack.js')
    let dd = await import('../public/pkg/wasm_pack_bg.wasm')
    return () => (
      <Ske>
        <Juicy www={cc} mmm={dd} />
      </Ske>
    )
  },
})
@inject('store')
@observer
class Juicy extends React.Component {
  constructor(props) {
    super(props)
    this.canvasRef = React.createRef();
  }
  draw2canvas=( file, canvEle )=>{
    console.log('here')
    let pic = document.createElement('image')
    pic.src = window.URL.createObjectURL(file)
    pic.onload=()=>{
      //canvEle.width = image.width
      //canvEle.height= image.height
      canvEle.getContext('2d').drawImage(pic,0,0)      
    }
    document.body.appendChild(pic)
  }
  render() {
    return (
      <div id="index_wrapper">
        <div>
          <Upload
            fileList={[]}
            beforeUpload={file=>{
              console.log(file)
              this.draw2canvas(file, this.canvasRef.current)
            }}
          >
            <Button>
              Upload ur Pic
            </Button>
          </Upload>
        </div>
        <div id="canvas_wrapper">
          <canvas ref={this.canvasRef}/>
        </div>
        <img id='shadow'/>
      </div>
    )
  }
  componentDidMount() {
    console.log('canvas!',this.canvasRef)
    console.log(this.props.www)
    console.log(this.props.mmm)
  }
}
export default RustComponent
